import colors from "./colors";

const input = {
  inputCoAir: {
    backgroundColor: "#FFFFFF",
    color: colors.blue2,
    overflow: "hidden",
    borderColor: colors.blue2,
    borderWidth: 3,
    borderRadius: 7,
    fontSize: 18,
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginVertical: 5,
    textAlign: "center",
  },
};
export default input;
