export const formaterHeureLocale = (date) => {
  return date.toLocaleTimeString("fr-FR", {
    hour: "2-digit",
    minute: "2-digit",
  });
};

export const calculerDF = (ds, dt) => {
  const df = new Date(ds.getTime() + dt * 60000);
  return df;
};

export const calculerDTR = (dr, pal, chPal) => {
  const totalPaliers = pal.reduce((acc, curr) => acc + curr, 0);
  const totalChPal = chPal.reduce((acc, curr) => acc + curr, 0);
  return dr + totalPaliers + totalChPal;
};

export const calculerHS = (df, dtr) => {
  return new Date(df.getTime() + dtr * 60000);
};
