import colors from "./colors";

const button = {
  button: {
    backgroundColor: colors.blue2,
    padding: 10,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
  },
  buttonText: {
    color: "#fff",
    textTransform: "uppercase",
    fontSize: 16,
  },
};
export default button;
